import React, { useState, useEffect } from "react";
import { navigate } from "@reach/router";

import { notion, useNotion } from "../services/notion";
import { Nav } from "../components/Nav";

export function Focus() {
  const { user } = useNotion();
  const [focus, setFocus] = useState(0);
  const [color, setColor] = useState('');
  const [focusScores, setFocusScores] = useState([]); // Step 1: Initialize state for focus scores list
  const [average, setAverage] = useState(0);

  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user]);

  useEffect(() => {
    if (!user) {
      return;
    }

    const subscription = notion.focus().subscribe((focus) => {
      const focusScore = Math.trunc(focus.probability * 100);
      setFocus(focusScore);

      // Append the new focus score to the focusScores list
      setFocusScores((prevScores) => [...prevScores, focusScore]);

      // Step 1: Create a function to calculate the average of an array
      function calculateAverage(array) {
        const sum = array.reduce((a, b) => a + b, 0);
        return sum / array.length;
      }

      // In your component
      // Step 2: Use the slice method to get the last 20 scores from the focusScores array
      const last20Scores = focusScores.slice(Math.max(focusScores.length - 20, 0));

      // Step 3: Call the average function on this sliced array
      const average = calculateAverage(last20Scores);
      setAverage(average);

      if (focusScore < 30) {
        setColor('red');
      } else {
        setColor('green');
      }
    });

    return () => {
      subscription.unsubscribe();
    };
  }, [user]);

  return (
    <main className="main-container">
      {user ? <Nav /> : null}
      <div className="metric-score" style={{ borderColor: color }}>
        &nbsp;{focus}% <div className="metric-word">Focus</div>
      </div>
      <div>Average of last 20 scores: {average}%</div>
    </main>
  );
}
