import React, { useState, useEffect } from "react";
import { navigate } from "@reach/router";




import { notion, useNotion } from "../services/notion";
import { Nav } from "../components/Nav";


export function Focus() {
 const { user } = useNotion();
 const [focus, setFocus] = useState(0);
 const [color, setColor] = useState('');


 useEffect(() => {
   if (!user) {
     navigate("/login");
   }
 }, [user]);


 useEffect(() => {
   if (!user) {
     return;
   }


   // const [color, setColor] = useState(''); // Step 1


   const subscription = notion.focus().subscribe((focus) => {
     const focusScore = Math.trunc(focus.probability * 100);
     // if focusScore is below 30, send a notification
     // for example, change the color of the border of metric-score div to red
     setFocus(focusScore);

     if (focusScore < 30) {
       setColor('red'); // Step 2
     } else {
       setColor('green'); // Reset color when focusScore is not below 30
     }
   });


   return () => {
     subscription.unsubscribe();
   };
 }, [user]);


 // function handleLowFocus(focusScore) {
 //   console.log(`Focus is low: ${focusScore}%`);
 //   // blabla whatever we want to call
 // }


 return (
   <main className="main-container">
     {user ? <Nav /> : null}
     <div className="focus-score" style={{ borderColor: color }}>
       &nbsp;{focus}% <div className="focus-word">Focus</div>
     </div>
   </main>
 );
}
