import React, { useState, useEffect } from "react";
import { navigate } from "@reach/router";
import { Line } from 'react-chartjs-2';

import { notion, useNotion } from "../services/notion";
import { Nav } from "../components/Nav";

export function Focus() {
  const { user } = useNotion();
  const [focusScores, setFocusScores] = useState([]);

  useEffect(() => {
    if (!user) {
      navigate("/login");
    }
  }, [user]);

  useEffect(() => {
    if (!user) {
      return;
    }

    const subscription = notion.focus().subscribe((focus) => {
      const focusScore = Math.trunc(focus.probability * 100);
      const now = new Date();
      setFocusScores(prevScores => [...prevScores, {time: now, score: focusScore}]);
    });

    return () => subscription.unsubscribe();
  }, [user]);

  const data = {
    labels: focusScores.map(d => d.time.toLocaleTimeString()),
    datasets: [
      {
        label: 'Focus Score',
        data: focusScores.map(d => d.score),
        fill: false,
        backgroundColor: 'rgb(75, 192, 192)',
        borderColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  return (
    <main className="main-container">
      {user ? <Nav /> : null}
      <div className="focus-score">
        &nbsp;{focusScores.length ? focusScores[focusScores.length - 1].score : 0}% <div className="focus-word">Focus</div>
      </div>
      <Line data={data} />
    </main>
  );
}
